/**
 * Captures a MIDI event from a specific device
 */
addEventListener($event, [{
  type: 'midi_arg_compare_device',
  condition: 'is',
  device: $device
}, $args], function (event) {
  var data = event.payload
  var args = event.args

  console.log('event', event)
  
  // Exit if midi_args fail
  var isValid = true
  event.args.forEach(function (arg) {
    switch (arg.type) {
      case 'midi_arg_compare_device':
        if (!isAny(arg.device)
          && !compare(arg.device, arg.condition, data.target.id))
            isValid = false
      break
      case 'midi_arg_compare_note':
        if (!isAny(arg.note)
          && !isNote(arg.note, arg.condition, data.note))
            isValid = false
      break
    }
  })
  if (!isValid) return

  $statements()
})

